const PlaylistCardPage = () => {
  const [playlists, setPlaylists] = useState([]);
  const [cookie, setCookie] = useCookies(["token"])
  useEffect(()=> {
    const getData = async () => {
      if(cookie.token){
        const response = await getMyPlaylist(cookie.token)
        setPlaylists(response.data?.playlistDetails)
      }
    }
    getData()
  }, [])
  if(!playlists){
    return
  }  
  return (
    <div className='h-full w-full overflow-auto rounded-xl bg-gradient-to-b from-[#1f2728] via-[#131618] to-[#070808]'>
      <PlaylistView title="Focus" data={focusData} />
      <PlaylistView title="Spotify Playlist" data={focusData} />
      <PlaylistView title="Sound of India" data={focusData} />
      <div>
        <div className='text-2xl font-semibold pb-5 pl-5 text-white '>My Playlists</div>
        <div className='flex gap-5 flex-wrap'>
          {
            playlists.map((item, index) => (
              <div key={index} className='bg-gradient-to-b from-[#1f2728] via-[#131618] to-[#070808] cursor-pointer opacity-80 w-[20%] py-5 space-y-2 p-4 hover:opacity-95 hover:transition-all delay-110 h-full transition-all rounded-lg hover:bg-slate-600'>
                <div className='mb-2'>
                  <img className='w-full rounded-lg h-auto' src={item.thumbnail} alt="" />
                </div>
                <div className='text-white'>{item.name}</div>
                {/* <div className='text-gray-400 text-sm'>{description}</div> */}
              </div>
            ))
          }
        </div>
      </div>
    </div>
  )
}

export default PlaylistCardPage